     and status = #{status}
     and is_starred = #{isStarred}
     and is_important = #{isImportant}

        and date_format(send_time,'%y%m%d') >= date_format(#{params.beginSendTime},'%y%m%d')


        and date_format(send_time,'%y%m%d') <= date_format(#{params.endSendTime},'%y%m%d')


order by send_time desc



    and status = 'deleted'
     and from_address like concat('%', #{fromAddress}, '%')
     and to_address like concat('%', #{toAddress}, '%')
     and subject like concat('%', #{subject}, '%')
     and email_type = #{emailType}
     and is_starred = #{isStarred}
     and is_important = #{isImportant}

        and date_format(delete_time,'%y%m%d') >= date_format(#{params.beginDeleteTime},'%y%m%d')


        and date_format(delete_time,'%y%m%d') <= date_format(#{params.endDeleteTime},'%y%m%d')


order by delete_time desc

mapper.update:
update email_personal
from_address = #{fromAddress}, to_address = #{toAddress}, subject = #{subject}, content = #{content}, html_content = #{htmlContent}, status = #{status}, is_starred = #{isStarred}, is_important = #{isImportant}, receive_time = #{receiveTime}, send_time = #{sendTime}, email_type = #{emailType}, attachment_count = #{attachmentCount}, delete_time = #{deleteTime}, create_by = #{createBy}, create_time = #{createTime}, update_by = #{updateBy}, update_time = #{updateTime}, remark = #{remark},
where email_id = #{emailId}
