status = #{status}, sales_date = #{salesDate}, product_model = #{productModel}, sales_quantity = #{salesQuantity}, play_count = #{playCount}, conversion_rate = #{conversionRate}, discount_type = #{discountType}, discount_rate = #{discountRatio}, source_channel = #{sourceChannel}, remark = #{remark}, update_by = #{updateBy}, update_time = #{updateTime},
where sales_id = #{salesId}
