mapper.update:
update email_send_reply_tag
contact_id = #{contactId}, tag_name = #{tagName}, tag_type = #{tagType}, send_count = #{sendCount}, reply_count = #{replyCount}, open_count = #{openCount}, last_send_time = #{lastSendTime}, last_reply_time = #{lastReplyTime}, remark = #{remark}, update_by = #{updateBy}, update_time = #{updateTime},
where tag_id = #{tagId}
